#include <string>
#include <iostream>
#include <vector>
#include <cstdlib>
#include <cstdio>
#include <ctime>
using namespace std;

vector<int> getNext(string str){
	vector<int> next(str.length() + 1);
	int strLen = str.length();
	next[0] = -1;
	int j = 0;
	for (j = 1; j <= strLen; j++){
		int k = next[j - 1];
		if (k == -1){
			next[j] = 0;
			continue;
		}
		while (k != -1) {
			if (str[j - 1] == str[k])
			{
				break;
			}
			k = next[k];
		}
		next[j] = k + 1;
	}

	return next;
}
