int main(){
	int T = 1;
	scanf("%d", &T);
	getchar(); //getchar();
	while (T--){
		string pattern, str;


		getline(cin, pattern);
		getline(cin, str);

		// 		str = "BBC ABCDAB ABCDABCDABDE";
		// 		pattern = "ABCDABD";
		int res = 0;
		vector<int> sheet = getNext(pattern);
		int s = 0, p = 0;
		while (s < str.length())
		{
			//if (s >= str.length()) break;
			if (str[s] != pattern[p])
			{
				if (p == 0)
				{
					s++; 
					continue;
				}
				p -= (p - sheet[p]);
				continue;

			}
			else {
				if (p == pattern.length() - 1)
				{
					res++;
					//printf("%d\n", s-p);
				}
				s++; p++;
			}
		}
		printf("%d\n", res);
	}
	return 0;
}